import './App.css';
import {useEffect, useState} from "react";

function App() {
  const URL = "https://newsapi.org/v2";
  const APIKEY = "0525752086b04125a9ff8e2c62c59c18";


  const [items, setItems] = useState([]);
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false)
  const [selectedItem, setSelectedItem] = useState(null)

  useEffect(() => {
    const CRITERIA = "/top-headlines?country=gb&category=technology"
    const ADDRESS = URL + CRITERIA + "&apiKey=" + APIKEY
    fetch(ADDRESS)
    .then(res => res.json())
    .then(
      (result) => {
        setError(null);
        setIsLoaded(true);
        if (typeof result.articles === "undefined"){
          setError({"message" : "Error retrieving data"})
        } else {
          setItems(result.articles)
        }

      }, (error) => {
        setError(error);
        setIsLoaded(true);
        setItems([])
      }
    )


  }, [])

  function close() {
    setSelectedItem(null)
  }

  if(setSelectedItem != null) {
    return <Details
      title={setSelectedItem.title}
      image={setSelectedItem.image}
      description={setSelectedItem.description}
    ></Details>
  } else if (error) {
    return <p>{error.message}</p>
  } else if (!isLoaded) {
    return <p>Ladataan...</p>
  } else {
    return (
      <div>
        {items.map(item=> (
          <div key={item.title} onClick={e => setSelectedItem(item)}>
            <h3>{item.title}</h3>
            <img src={item.urlToImage}></img>
            <p>{item.description}</p>
          </div>
        ))}
      </div>
    );
  }
}

export default App;
