import './App.css';
import {useEffect, useState} from "react";

function App() {
  const URL = "www.thecocktaildb.com/api/json/v1/";
  const APIKEY = "1";


  const [items, setItems] = useState([]);
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false)

  useEffect(() => {
    const CRITERIA = "/random.php"
    const ADDRESS = "www.thecocktaildb.com/api/json/v1/1/search.php?s=margarita"
    fetch(ADDRESS)
    .then(res => res.json())
    .then(
      (result) => {
        setError(null);
        setIsLoaded(true);
        setItems(result.drinks)
      }, (error) => {
        setError(error);
        setIsLoaded(true);
        setItems([])
      }
    )


  }, [])


  if (error) {
    return <p>{error.message}</p>
  } else if (!isLoaded) {
    return <p>Ladataan...</p>
  } else {
    return (
      <div>
        {items.map(item => {
          <div key={item.strDrink}>

          </div>
        })}
      </div>
    );
  }

}

export default App;
